mapper.select:
SELECT ord_code, mbl_id, ord_name, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_tel, ord_price, ord_desc, ord_regdate FROM ( SELECT /*+ INDEX_DESC(order_tbl pk_ord_code) */ ROWNUM AS RN, ord_code, mbl_id, ord_name, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_tel, ord_price, ord_desc, ord_regdate FROM order_tbl WHERE
ROWNUM <= (#{cri.pageNum} * #{cri.amount}) -- 페이지번호 * 페이지별 출력건수
) WHERE RN > (#{cri.pageNum} - 1 ) * #{cri.amount}

mapper.delete:

DELETE FROM 
	ordetail_tbl
WHERE
	ord_code = #{ord_code}
AND
	pro_num = #{pro_num}

mapper.update:

UPDATE
	order_tbl
SET
	ord_price = (
					SELECT SUM(dt_amount * dt_price) FROM ordetail_tbl WHERE ord_code = #{ord_code}
				)
WHERE
	ord_code = #{ord_code}
