mapper.select:
SELECT qna_code, mbl_id, pro_num, qna_title, qna_content, qna_date, ans_content, ans_check, ans_date, admin_id FROM ( SELECT /*+ INDEX_DESC(qna_tbl pk_qna_code) */ ROWNUM AS RN, qna_code, mbl_id, pro_num, qna_title, qna_content, qna_date, ans_content, ans_check, ans_date, admin_id FROM qna_tbl WHERE
ROWNUM <= (#{pageNum} * #{amount}) -- 페이지번호 * 페이지별 출력건수
) WHERE RN > (#{pageNum} - 1 ) * #{amount}
