mapper.update:

UPDATE
	mbl_tbl
SET
	mbl_email = #{mbl_email}, mbl_zip_code = #{mbl_zip_code}, mbl_addr = #{mbl_addr}, mbl_addr_detail = #{mbl_addr_detail}, mbl_phone = #{mbl_phone}
WHERE
	 mbl_id = #{mbl_id}

mapper.select:

SELECT 
	mbl_email
FROM 
	mbl_tbl
WHERE
	mbl_id = #{mbl_id}
AND
	mbl_name = #{mbl_name}
AND
	mbl_email = #{mbl_email}

mapper.update:

UPDATE
	mbl_tbl
SET
	mbl_password = #{temp_enc_pw}
WHERE
	mbl_id = #{mbl_id}


UPDATE 
	mbl_tbl
SET
	mbl_password = #{new_mbl_password}
WHERE
	mbl_id = #{mbl_id}
