mapper.select:
SELECT qna_code, mbl_id, pro_num, qna_title, qna_content, qna_date, admin_id, ans_content, ans_check, ans_date FROM ( SELECT /*+ INDEX_DESC(review_tbl pk_review_code) */ ROWNUM AS RN, qna_code, mbl_id, pro_num, qna_title, qna_content, qna_date, admin_id, ans_content, ans_check, ans_date FROM qna_tbl WHERE pro_num = #{pro_num} AND
ROWNUM <= (#{cri.pageNum} * #{cri.amount}) -- 페이지번호 * 페이지별 출력건수
) WHERE RN > (#{cri.pageNum} - 1 ) * #{cri.amount}
